mapper.select:
{
    call PKG_MOVIECAST.SELECT_MOVIECAST(
         #{castIdx, jdbcType=VARCHAR, mode=IN}
        ,#{castId, jdbcType=VARCHAR, mode=IN}
        ,#{DOCID, jdbcType=VARCHAR, mode=IN}
        ,#{role, jdbcType=VARCHAR, mode=IN}
        ,#{roleDetail, jdbcType=VARCHAR, mode=IN}
        ,#{refDate, jdbcType=VARCHAR, mode=IN}
        ,#{result, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=moviecastMap}
)}

{
